mapper.select:
SELECT id, title, img_url, video_url, content, type, category_id
FROM baking.content
WHERE id = #{id}

mapper.update:
UPDATE baking.content
title=#{title}, content=#{content}, img_url=#{imgUrl}, video_url=#{videoUrl}, brief=#{brief}, category_id=#{categoryId}, update_time=#{updateTime}
WHERE id = #{id}
